const express = require('express');
const router = express.Router();
const { createPlan, getPlans, getPlanwithid, updatePlan } = require('../services/planService');

// Create Plan
router.post('/add', async (req, res) => {
    try {
        const plan = await createPlan(req.body);
        res.status(201).json(plan);
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
});

// Get All Plans
router.get('/getall', async (req, res) => {
    try {
        const plans = await getPlans();
        console.log(plans)
        res.status(200).json(plans);
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
});


router.get('/get/:id', async (req, res) => {
    try {
        
        const { id } = req.params; // Extract `id` from the request parameters
        const plan = await getPlanwithid(id); // Call your service function
        if (!plan) {
            return res.status(404).json({ message: 'Plan not found' }); // Handle case when no plan is found
        }
        res.status(200).json(plan); // Send the retrieved plan as response
    } catch (error) {
        res.status(500).json({ error: error.message }); // Handle errors
    }
});

router.put('/update/:id', async (req, res) => {
    try {
        const { id } = req.params; // Extract `id` from the route parameters
        const updateData = req.body; // Get update data from the request body

        // Call the service function
        const updatedPlan = await updatePlan(id, updateData);

        res.status(200).json({
            message: 'Plan updated successfully',
            data: updatedPlan,
        });
    } catch (error) {
        res.status(500).json({ error: error.message }); // Handle errors
    }
});




module.exports = router;
