const Plan = require('../models/plan');

const createPlan = async (planData) => {
    return await Plan.create(planData);
};

const getPlans = async () => {
    return await Plan.findAll();
};

const getPlanwithid = async (id) => {
    try {
        // Fetch plan by ID
        const plan = await Plan.findOne({ where: { plan_id: id } });
        return plan;
    } catch (error) {
        console.error('Error fetching plan:', error.message);
        throw error; 
    }
};


const updatePlan = async (id, updateData) => {
    try {
        const plan = await Plan.findOne({ where: { plan_id: id } }); // Find the plan by ID
        if (!plan) {
            throw new Error('Plan not found'); // Throw an error if plan doesn't exist
        }
        await plan.update(updateData); // Update the plan with the provided data
        return plan; // Return the updated plan
    } catch (error) {
        throw new Error('Error updating plan: ' + error.message); // Handle and propagate errors
    }
};


module.exports = { createPlan, getPlans, getPlanwithid, updatePlan };
